import React, {useState} from "react";
import {Pie} from "@visx/shape";
import {Group} from "@visx/group"
import {Text } from "@visx/text"

const coins = [
    { symbol: "ADA", amount: 200, color: "#0033ad", inUSD: 1.48 },
    { symbol: "SOL", amount: 5, color: "#00ffbd", inUSD: 37.6 },
    { symbol: "BTC", amount: 0.005, color: "#F7931A", inUSD: 37363 },
  ];

function PieChart(){
    const width = 200;
    const half = width /2;
    return(
        <div>

            <svg width={width} height={width}> 

                <group top={half} left={half}>
                    <pie data={coins} pieValue ={(data) => data.amount *data.inUSD} outerRadius={half} 
                    ></pie>
                </group>

            
            </svg>

        </div>
    )

}


export default PieChart
