import React, {useState} from "react";
import {Pie} from "@visx/shape";
import {Group} from "@visx/group"
import {Text } from "@visx/text"

const coins = [
    { symbol: "ADA", amount: 200, color: "#0033ad", inUSD: 1.48 },
    { symbol: "SOL", amount: 5, color: "#00ffbd", inUSD: 37.6 },
    { symbol: "BTC", amount: 0.005, color: "#F7931A", inUSD: 37363 },
  ];

function PieChart(){
    const [active, setActive] = useState(null);
    const width = 200;
    const half = width /2;
    return(
        <div>

            <svg width={width} height={width}> 

                <Group top={half} left={half}>
                    <Pie 
                        data={coins} 
                        pieValue ={(data) => data.amount *data.inUSD} 
                        outerRadius={half}
                        innerRadius={half-8}
                        paddAngle = {.01}
                    
                    >
                        {(pie) => {
                            return pie.arcs.map(arc =>{
                                return <g key= {arc.data.symbol} onMouseEnter={() => setActive()}>
                                    <path d={pie.path(arc)} fill={arc.data.color}></path>
                                </g>
                            })
                        }

                        }

                    </Pie>
                </Group>

            
            </svg>

        </div>
    )

}


export default PieChart
