import { render } from 'react-dom';
import React, {Component, useState, useEffect} from 'react'
import './App.css'
import Web3 from 'web3'
import {injected} from "./connectors.js"
import {useWeb3React, Web3ReactProvider} from '@web3-react/core'
import  {tokenAddresses, erc20ABI} from './tokens.js'
import Sidebar from './Components/Sidebar.js'
import CoinCard from './Components/CoinCard';
import Display from './Components/Display';
import axios from 'axios'
import getTokenPrices from './Components/helper';

class App extends React.Component{
    
    constructor(props){
        super(props)
        this.state = {
            account:'',
            metaMaskInstalled:'',
            tokens:["Start"],
            tokenABI:'',
        }

        const [coins, setCoin] = useState([]);
        const [search, setSearch] = useState('')
    
    
        useEffect( () =>{
            axios.get('https://api.coingecko.com/api/v3/coins/markets?vs_currency=USD&order=market_cap_desc&per_page=100&page=3&sparkline=true'). then(res =>{
                setCoin(res.data);
                
            }).catch(error=> console.log(error))
        }, []);
    
        const handleChange = e => {
            setSearch(e.target.value)
        }
    
        const filteredCoins = coins.filter(coin =>
            coin.name.toLowerCase().includes(search.toLowerCase())
        )

    }

    render(){

        return(            
            <div className="App">
                <Sidebar/>
                <div className= "content">
                    <getTokenPrices/>

                    <h1>Address: {this.state.account} </h1>
                    <button onClick={this.connectWallet}> Connect Wallet`</button>
                    <h1>Tokens: {this.state.tokens}</h1>
                    <Display/>
                    <CoinCard/>
                </div>
                <div className="SidePanel">

                </div>
            </div>
        )
    }

}
export default App
