import { render } from 'react-dom';
import React, {Component} from 'react'
import './App.css'
import Web3 from 'web3'
import {injected} from "./connectors.js"
import {useWeb3React, Web3ReactProvider} from '@web3-react/core'


class App extends React.Component{
 
    // const { active, account, library, connector, activate, deactivate} = useWeb3React()
    
    /*
    async UNSAFE_componentWillMount(){
       // await this.tryConnect()
        //await this.loadBlockchainData()
    }*/

   async connectWallet(){
        const metaMaskInstalled = typeof window.ethereum !== 'undefined'
        this.setState({metaMaskInstalled: metaMaskInstalled})
        if(metaMaskInstalled){
            await this.loadWeb3()
            await this.loadBlockchainData() 
        }        
    }
    
   //var strongAddress = '0x990f341946A3fdB507aE7e52d17851B87168017c';
    
    async loadBlockchainData(){
        var tokenABI = [
            {
                "constant": true,
                "inputs": [],
                "name": "name",
                "outputs": [
                    {
                        "name": "",
                        "type": "string"
                    }
                ],
                "payable": false,
                "stateMutability": "view",
                "type": "function"
            },
            {
                "constant": false,
                "inputs": [
                    {
                        "name": "_spender",
                        "type": "address"
                    },
                    {
                        "name": "_value",
                        "type": "uint256"
                    }
                ],
                "name": "approve",
                "outputs": [
                    {
                        "name": "",
                        "type": "bool"
                    }
                ],
                "payable": false,
                "stateMutability": "nonpayable",
                "type": "function"
            },
            {
                "constant": true,
                "inputs": [],
                "name": "totalSupply",
                "outputs": [
                    {
                        "name": "",
                        "type": "uint256"
                    }
                ],
                "payable": false,
                "stateMutability": "view",
                "type": "function"
            },
            {
                "constant": false,
                "inputs": [
                    {
                        "name": "_from",
                        "type": "address"
                    },
                    {
                        "name": "_to",
                        "type": "address"
                    },
                    {
                        "name": "_value",
                        "type": "uint256"
                    }
                ],
                "name": "transferFrom",
                "outputs": [
                    {
                        "name": "",
                        "type": "bool"
                    }
                ],
                "payable": false,
                "stateMutability": "nonpayable",
                "type": "function"
            },
            {
                "constant": true,
                "inputs": [],
                "name": "decimals",
                "outputs": [
                    {
                        "name": "",
                        "type": "uint8"
                    }
                ],
                "payable": false,
                "stateMutability": "view",
                "type": "function"
            },
            {
                "constant": true,
                "inputs": [
                    {
                        "name": "_owner",
                        "type": "address"
                    }
                ],
                "name": "balanceOf",
                "outputs": [
                    {
                        "name": "balance",
                        "type": "uint256"
                    }
                ],
                "payable": false,
                "stateMutability": "view",
                "type": "function"
            },
            {
                "constant": true,
                "inputs": [],
                "name": "symbol",
                "outputs": [
                    {
                        "name": "",
                        "type": "string"
                    }
                ],
                "payable": false,
                "stateMutability": "view",
                "type": "function"
            },
            {
                "constant": false,
                "inputs": [
                    {
                        "name": "_to",
                        "type": "address"
                    },
                    {
                        "name": "_value",
                        "type": "uint256"
                    }
                ],
                "name": "transfer",
                "outputs": [
                    {
                        "name": "",
                        "type": "bool"
                    }
                ],
                "payable": false,
                "stateMutability": "nonpayable",
                "type": "function"
            },
            {
                "constant": true,
                "inputs": [
                    {
                        "name": "_owner",
                        "type": "address"
                    },
                    {
                        "name": "_spender",
                        "type": "address"
                    }
                ],
                "name": "allowance",
                "outputs": [
                    {
                        "name": "",
                        "type": "uint256"
                    }
                ],
                "payable": false,
                "stateMutability": "view",
                "type": "function"
            },
            {
                "payable": true,
                "stateMutability": "payable",
                "type": "fallback"
            },
            {
                "anonymous": false,
                "inputs": [
                    {
                        "indexed": true,
                        "name": "owner",
                        "type": "address"
                    },
                    {
                        "indexed": true,
                        "name": "spender",
                        "type": "address"
                    },
                    {
                        "indexed": false,
                        "name": "value",
                        "type": "uint256"
                    }
                ],
                "name": "Approval",
                "type": "event"
            },
            {
                "anonymous": false,
                "inputs": [
                    {
                        "indexed": true,
                        "name": "from",
                        "type": "address"
                    },
                    {
                        "indexed": true,
                        "name": "to",
                        "type": "address"
                    },
                    {
                        "indexed": false,
                        "name": "value",
                        "type": "uint256"
                    }
                ],
                "name": "Transfer",
                "type": "event"
            }
        ]
        const web3 = window.web3
        // This gets a list of the accounts for this wallet
        const account = await web3.eth.getAccounts();
        const balance = await web3.eth.getBalance(account.toString());
        console.log("Account: "+account+" Balance: "+balance);
        this.setState({account: account});
        console.log("state acct: "+ this.state.account);
        const tokenInst = new web3.eth.Contract(tokenABI, "0x990f341946A3fdB507aE7e52d17851B87168017c");
        const strongBal = await tokenInst.methods.balanceOf(account.toString()).call();
        console.log("strong bal: "+strongBal);
        this.render();
    }
    async loadWeb3(){
        console.log("loading web3");
        if(window.ethereum){
            
            window.web3 = new Web3(window.ethereum)
            
            await window.ethereum.enable()
            
        }else if(window.web3 ){

            window.web3 = new Web3(window.web3.currentProvider)

        }else {

            window.alert('no ethereum browser detected')
        }
    } 
    async tryConnect(){
        if(window.ethereum){
            window.web3 = new Web3(window.ethereum)
            await window.ethereum.enable()
        }else if(window.web3){

            window.web3 = new Web3(window.web3.currentProvider)

        }else{
            window.alert("no ethereum")
        }
    }

    constructor(props){
        super(props)
        this.state = {
            account:'',
            metaMaskInstalled:''
        }
    }


    render(){

        return(
            
            <div>
                
                <h1>Address: {this.state.account} </h1>
                <button onClick={this.connectWallet}> Connect Wallet`</button>
            </div>
        )
    }

}
export default App
