import React, {Component, useState, useEffect} from "react";
import axios from "axios";
import { get } from "https";


function getTokenPrices(){

    const [coin, setCoin] = useState([]);
    const [search, setSearch] = useState('')


    useEffect( () =>{
        axios.get('https://api.coingecko.com/api/v3/coins/markets?vs_currency=USD&order=market_cap_desc&per_page=100&page=3&sparkline=true'). then(res =>{
            setCoin(res.data);
            
        }).catch(error=> console.log(error))
    }, []);

    const handleChange = e => {
        setSearch(e.target.value)
    }

    const filteredCoins = coins.filter(coin =>
        coin.name.toLowerCase().includes(search.toLowerCase())
        )

    return(

        <div>
            <form> 
                <input type="text" placeholder="Enter Token" className="coinInput" onChange={handleChange}/>
            </form>
            {filteredCoins.map(coin =>{
                return (
                    <CoinCard key={coin.id} name={coin.name} price={coin.price} image={coin.image}/>
                )
            })}
        </div>
        
    )

}

export default getTokenPrices
