import React, {Component} from 'react'
import {SidebarData} from "./SidebarData"
import './../Style/Sidebar.css'
import * as FiIcons from "react-icons/fi";
import { Link } from 'react-router-dom';

function toggleIcon(icon){

    listItem = document.getElementById(icon);
    if(listItem == null) return;
    if(document.getElementById(icon).classList.contains('selected'))
    {
        document.getElementById(icon).classList.remove('selected')
    }else{
        document.getElementById(icon).classList.add('selected')
    }
    
}

function Sidebar(){

    return(
        <div className='Sidebar'>
            <div>
                <FiIcons.FiAperture/>
            </div>
        
            <div className="SidebarList">
                <ul>
                    <li className ="Selected" id="home"> 
                        <button onClick={toggleIcon('home')}><FiIcons.FiHome/></button>
                    </li>
                    <li className ="" id="Portfolio">
                        <button onClick={toggleIcon('portfolio')}><FiIcons.FiActivity/></button>
                    </li>
                    <li className ="" id="Wallet">
                        <button onClick={toggleIcon('wallet')}><FiIcons.FiDollarSign/></button>
                    </li>
                </ul>
            </div>
        </div>
    )

}

export default Sidebar

